import React, { Component } from 'react'
import { Container, Spinner } from 'reactstrap'
import styled from 'styled-components'
import api from '../api/index'

const BannerFull = styled.div`
	background: #FFF;
	height: 500px;
	margin-top: -50px;
	display: grid;
	grid-template-rows: auto 150px 150px;
`
const CarouselItem = (props) => {
	return (
		<a href={props.data.url}>
			<div className="carousel-item">
				<img className="d-block w-100" src={"/images/carousel/" + props.data.image} alt={props.data.title} />
				<div className="carousel-caption d-none d-md-block">
					<h5>{props.data.title}</h5>
				</div>
			</div>
		</a>
	)
}
export default class Section extends Component {
	constructor(props) {
		super(props)

		this.state = { 
			images: [],
			isLoading: false
		}
	}
	componentWillMount = async () => {
		this.setState({ isLoading: true })
		await api.getCarousel().then(carousel => {
			this.setState({
				images: carousel.data[0].carousel,
				isLoading: false
			})
		})
	}
	render () {
		const {images, isLoading} = this.state
		return (
			<section className="pt-3">
				<BannerFull className="container">
					<div id="carousel"></div>
					<div id="teste"></div>
					<div id="search">
						<form>
							<div class="row">
								<div class="col-sm">
									<input type="text" class="form-control" placeholder="Cidade" aria-label="Cidade"/>
  								</div>
								<div class="col-sm">
									<input type="text" class="form-control" placeholder="Bairros" aria-label="Bairros"/>
  								</div>
								<div class="col-sm">
									<input type="text" class="form-control" placeholder="Tipos" aria-label="Tipos"/>
  								</div>
								<div class="col-sm">
									<input type="text" class="form-control" placeholder="Quartos" aria-label="Quartos" />
								</div>
								<div class="col-sm">
									<label for="price_range" class="form-label">Pre√ßo</label>
									<input type="range" class="form-range" min="0" max="5000" step="100" id="price_range"/>
								</div>
							</div>
						</form>
					</div>
				</BannerFull>
				<Container fluid>
					{images.status
						? <div id="my-carousel" className="carousel slide" data-ride="carousel">
								<div className="carousel-inner">
									{isLoading
										? <Spinner style={{ width: '3rem', height: '3rem' }} />
										: images.imagens
											.map(data => <CarouselItem data={data} />)
									}
								</div>
							</div>
						: null
					}
				</Container>
			</section>
		)
	}
}
